                          active={oneBa}
                          onClick={handleBa}
                        >
                          1
                        </Button>
                      </>
                    </div>
                  </Col>
                  <Col lg="2" sm="6" xs="6">
                    <div className="text-center bedrooms-bathrooms-btn-row-align">
                      <>
                        <Button
                          variant="primary"
                          className="bedrooms-bathrooms-btn-white"
                          type="button"
                          data-toggle="button"
                          name="bathrooms"
                          id="bathrooms"
                          value="2"
                          active={twoBa}
                          onClick={handleBa}
